    const userId = response2.data.token_info.user_id;

    if (!loginToken || !userId) {
      console.error('登录失败：token信息不完整');
      throw new Error('登录失败：token信息不完整');
    }

    console.log('登录成功,获取到loginToken和userId');
    return { loginToken, userId };
  } catch (error) {
    console.error('登录失败:', error.message);
    if (error.response) {
      console.error('错误响应状态码:', error.response.status);
      console.error('错误响应头:', error.response.headers);
      console.error('错误响应数据:', error.response.data);
    }
    throw error;
  }
}

// 获取app token
async function getAppToken(loginToken) {
  try {
    // 直接从登录响应中获取app_token
    const url = `https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token=${loginToken}`;

    console.log('获取appToken请求URL:', url);

    const response = await axios.get(url, {
      headers,
      validateStatus: function (status) {
        return status >= 200 && status < 400;
      }
    });

    console.log('获取appToken响应状态码:', response.status);
    console.log('获取appToken响应头:', response.headers);
    console.log('获取appToken响应数据:', response.data);

    if (!response.data || !response.data.token_info) {
      console.error('获取appToken失败：未获取到token信息');
      throw new Error('获取appToken失败：未获取到token信息');
    }

    const appToken = response.data.token_info.app_token;
    if (!appToken) {
      console.error('获取appToken失败：token信息不完整');
      throw new Error('获取appToken失败：token信息不完整');
    }

    console.log('获取appToken成功:', appToken);
    return appToken;
  } catch (error) {
    console.error('获取appToken失败:', error.message);
    if (error.response) {
      console.error('错误响应状态码:', error.response.status);
      console.error('错误响应头:', error.response.headers);
      console.error('错误响应数据:', error.response.data);
    }
    throw error;
  }
}

// 获取时间戳
async function getTime() {
  try {
    const response = await axios.get('http://mshopact.vivo.com.cn/tool/config', { headers });
    return response.data.data.nowTime;
  } catch (error) {
    console.error('获取时间戳失败:', error.message);
    throw error;
  }
}

// 修改步数
async function updateSteps(loginToken, appToken, steps) {
  try {
    const today = moment().format('YYYY-MM-DD');
    console.log('当前日期:', today);
    console.log('目标步数:', steps);

    // 修改data_json格式
    const dataJson = JSON.stringify([{
      data_hr: "",
      date: today,
      data: [{
        start: 0,
        stop: 1439,
        value: "",
        tz: 32,
        did: "DA932FFFFE8816E7",
        src: 24
      }],
      summary: JSON.stringify({
        v: 6,
        slp: {
          st: 1628296479,
          ed: 1628296479,
          dp: 0,
          lt: 0,
          wk: 0,
          usrSt: -1440,
          usrEd: -1440,
          wc: 0,
          is: 0,
          lb: 0,
          to: 0,
          dt: 0,
          rhr: 0,
          ss: 0
        },
        stp: {
          ttl: steps,
          dis: Math.floor(steps * 0.6),
          cal: Math.floor(steps * 0.04),
          wk: 41,
          rn: 50,
          runDist: 7654,
          runCal: 397,
          stage: []
        },
        goal: 8000,
        tz: "28800"
      }),
      source: 24,
      type: 0
    }]);

    const timestamp = new Date().getTime();
    const t = String(parseInt((new Date).getTime() / 1000 + ''));

    const url = `https://api-mifit-cn2.huami.com/v1/data/band_data.json?t=${timestamp}`;
    const data = `userid=${loginToken}&last_sync_data_time=${t}&device_type=0&last_deviceid=DA932FFFFE8816E7&data_json=${encodeURIComponent(dataJson)}`;

    console.log('更新步数请求URL:', url);
    console.log('更新步数请求数据:', data);

    const response = await axios.post(url, data, {
      headers: {
        ...headers,
        apptoken: appToken,
        'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
      },
      validateStatus: function (status) {
        return status >= 200 && status < 400;
      }
    });

    console.log('更新步数响应状态码:', response.status);
    console.log('更新步数响应头:', response.headers);
    console.log('更新步数响应数据:', response.data);

    if (response.data.code !== 1) {
      console.error('更新步数失败:', response.data);
      throw new Error('更新步数失败: ' + JSON.stringify(response.data));
    }

    console.log('更新步数成功');
    return response.data;
  } catch (error) {
    console.error('更新步数失败:', error.message);
    if (error.response) {
      console.error('错误响应状态码:', error.response.status);
      console.error('错误响应头:', error.response.headers);
      console.error('错误响应数据:', error.response.data);
    }
    throw error;
  }
}

// 主处理函数
module.exports = async (req, res) => {
  // 记录请求信息
  console.log('收到请求:', {
    method: req.method,
    url: req.url,
    headers: req.headers,
    body: req.body
  });

  // 设置CORS
  res.setHeader('Access-Control-Allow-Credentials', true);
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Methods', 'GET,OPTIONS,PATCH,DELETE,POST,PUT');
  res.setHeader('Access-Control-Allow-Headers', 'X-CSRF-Token, X-Requested-With, Accept, Accept-Version, Content-Length, Content-MD5, Content-Type, Date, X-Api-Version');

  // 处理OPTIONS请求
  if (req.method === 'OPTIONS') {
    console.log('处理OPTIONS请求');
    res.status(200).end();
    return;
  }

  // 只允许POST请求
  if (req.method !== 'POST') {
    console.log('拒绝非POST请求');
    res.status(405).json({ success: false, message: 'Method not allowed' });
    return;
  }

  try {
    const { email, password, steps } = req.body;
    console.log('请求参数:', { email, steps });

    // 参数验证
    if (!email || !password) {
      console.log('参数验证失败: 账号或密码为空');
      res.status(400).json({ success: false, message: '账号和密码不能为空' });
      return;
    }

    // 验证账号格式
    const isPhone = /^\+?\d+$/.test(email);
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!isPhone && !emailRegex.test(email)) {
      console.log('参数验证失败: 账号格式不正确');
      res.status(400).json({ success: false, message: '账号格式不正确，请输入手机号或邮箱' });
      return;
    }

    // 如果是手机号且没有+86前缀，自动添加
    if (isPhone && !email.startsWith('+86')) {
      email = '+86' + email;
      console.log('自动添加+86前缀后的手机号:', email);
    }

    // 设置默认步数
    const targetSteps = steps || Math.floor(Math.random() * 10000) + 20000;
    console.log('目标步数:', targetSteps);

    // 登录获取token
    console.log('开始登录流程...');
    const { loginToken, userId } = await login(email, password);
    console.log('登录成功,获取到loginToken和userId');

    // 获取app token
    console.log('开始获取appToken...');
    const appToken = await getAppToken(loginToken);
    console.log('获取appToken成功');

    // 修改步数
    console.log('开始更新步数...');
    const result = await updateSteps(loginToken, appToken, targetSteps);
    console.log('步数更新结果:', result);

    // 返回结果
    const response = {
      success: true,
      message: `步数修改成功: ${targetSteps}`,
      data: result
    };
    console.log('返回响应:', response);
    res.status(200).json(response);
  } catch (error) {
    console.error('API处理失败:', error);
    const response = {
      success: false,
      message: error.message || '服务器内部错误'
    };
    console.log('返回错误响应:', response);
    res.status(500).json(response);
  }
};

async function main() {
  try {
    const account = 'miloce@vip.qq.com'; // 替换为你的账号
    const password = 'luozhiyun520.'; // 替换为你的密码
    const steps = 20000; // 设置想要的步数

    console.log('开始执行...');
    const { loginToken, userId } = await login(account, password);
    const appToken = await getAppToken(loginToken);
    const result = await updateSteps(loginToken, appToken, steps);

    console.log('执行成功:', result);
  } catch (error) {
    console.error('执行失败:', error);
  }
}

