// 主处理函数
module.exports = async (req, res) => {
  // 记录请求信息
  console.log('收到请求:', {
    method: req.method,
    url: req.url,
    headers: req.headers,
    body: req.body
  });

  // 设置CORS
  res.setHeader('Access-Control-Allow-Credentials', true);
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Methods', 'GET,OPTIONS,PATCH,DELETE,POST,PUT');
  res.setHeader('Access-Control-Allow-Headers', 'X-CSRF-Token, X-Requested-With, Accept, Accept-Version, Content-Length, Content-MD5, Content-Type, Date, X-Api-Version');

  // 处理OPTIONS请求
  if (req.method === 'OPTIONS') {
    console.log('处理OPTIONS请求');
    res.status(200).end();
    return;
  }

  // 只允许POST请求
  if (req.method !== 'POST') {
    console.log('拒绝非POST请求');
    res.status(405).json({ success: false, message: 'Method not allowed' });
    return;
  }

  try {
    const { email, password, steps } = req.body;
    console.log('请求参数:', { email, steps });

    // 参数验证
    if (!email || !password) {
      console.log('参数验证失败: 账号或密码为空');
      res.status(400).json({ success: false, message: '账号和密码不能为空' });
      return;
    }

    // 验证账号格式
    const isPhone = /^\+?\d+$/.test(email);
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!isPhone && !emailRegex.test(email)) {
      console.log('参数验证失败: 账号格式不正确');
      res.status(400).json({ success: false, message: '账号格式不正确，请输入手机号或邮箱' });
      return;
    }

    // 如果是手机号且没有+86前缀，自动添加
    if (isPhone && !email.startsWith('+86')) {
      email = '+86' + email;
      console.log('自动添加+86前缀后的手机号:', email);
    }

    // 设置默认步数
    const targetSteps = steps || Math.floor(Math.random() * 10000) + 20000;
    console.log('目标步数:', targetSteps);

    // 登录获取token
    console.log('开始登录流程...');
    const { loginToken, userId } = await login(email, password);
    console.log('登录成功,获取到loginToken和userId');

    // 获取app token
    console.log('开始获取appToken...');
    const appToken = await getAppToken(loginToken);
    console.log('获取appToken成功');

    // 修改步数
    console.log('开始更新步数...');
    const result = await updateSteps(loginToken, appToken, targetSteps);
    console.log('步数更新结果:', result);

    // 返回结果
    const response = {
      success: true,
      message: `步数修改成功: ${targetSteps}`,
      data: result
    };
    console.log('返回响应:', response);
    res.status(200).json(response);
  } catch (error) {
    console.error('API处理失败:', error);
    const response = {
      success: false,
      message: error.message || '服务器内部错误'
    };
    console.log('返回错误响应:', response);
    res.status(500).json(response);
  }
};

async function main() {
  try {
    const account = 'miloce@vip.qq.com'; // 替换为你的账号
    const password = 'luozhiyun520.'; // 替换为你的密码
    const steps = 20000; // 设置想要的步数

    console.log('开始执行...');
    const { loginToken, userId } = await login(account, password);
    const appToken = await getAppToken(loginToken);
    const result = await updateSteps(loginToken, appToken, steps);

    console.log('执行成功:', result);
  } catch (error) {
    console.error('执行失败:', error);
  }
}

main();
